package java_11_03;

public class SpecialChar {//특수문자 테스트

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("\'\'");		     // ''를 출력
		System.out.println("fkfkfk\t999\b1234"); // \t는 텝을 \b는 백스페이스를
		System.out.println("\"\"");	 //""  큰따옴표를 출력하려면 이렇게 한다.
		System.out.println("c:\\");//역슬래쉬를 출력할 때
	}
}
